import React, { useState, useEffect } from 'react';
import './Navbar.css';

const encryptText = (text) => {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()';
  return text.split('').map(char => chars[Math.floor(Math.random() * chars.length)]).join('');
};

const Navbar = () => {
  const [activeRound, setActiveRound] = useState('Home');
  const [encryptedTexts, setEncryptedTexts] = useState({});

  const navItems = ['Home', 'Folder', 'Connect', 'Music'];

  useEffect(() => {
    const encryptInterval = setInterval(() => {
      const newEncrypted = {};
      navItems.forEach(item => {
        newEncrypted[item] = encryptText(item);
      });
      setEncryptedTexts(newEncrypted);
    }, 100);

    return () => clearInterval(encryptInterval);
  }, []);

  const handleHover = (item) => {
    setActiveRound(item);
  };

  return (
    <header id="navbar">
      <nav className="navigation">
        {navItems.map(item => (
          <a 
            key={item}
            href={`#${item.toLowerCase()}`}
            className={activeRound === item ? 'round' : ''}
            onMouseEnter={() => handleHover(item)}
            data-hover={encryptedTexts[item] || item}
          >
            <span className="text-wrapper">{item}</span>
          </a>
        ))}
      </nav>
    </header>
  );
};

export default Navbar;
