import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [activeRound, setActiveRound] = useState('Home');
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  const handleHover = (item) => {
    setActiveRound(item);
  };

  const toggleMusic = (e) => {
    e.preventDefault();
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <header id="navbar">
      <nav className="navigation">
        <Link 
          to="/" 
          className={activeRound === 'Home' ? 'round' : ''}
          onMouseEnter={() => handleHover('Home')}
          data-hover="Home"
        >
          <span className="text-wrapper">Home</span>
        </Link>
        <Link 
          to="/folder" 
          className={activeRound === 'Folder' ? 'round' : ''}
          onMouseEnter={() => handleHover('Folder')}
          data-hover="Folder"
        >
          <span className="text-wrapper">Folder</span>
        </Link>
        <Link 
          to="/connect" 
          className={`${activeRound === 'Connect' ? 'round' : ''}`}
          onMouseEnter={() => handleHover('Connect')}
          data-hover="Connect"
        >
          <span className="text-wrapper">Connect</span>
        </Link>
        <a 
          href="#" 
          onClick={toggleMusic}
          className={`${activeRound === 'Music' ? 'round' : ''} music-tab ${isPlaying ? 'playing' : ''}`}
          onMouseEnter={() => handleHover('Music')}
          data-hover={isPlaying ? 'Pause' : 'Play'}
        >
          <span className="text-wrapper">Music</span>
        </a>
      </nav>
      <audio ref={audioRef} src="/AndanteC.mp3" loop />
    </header>
  );
};

export default Navbar;
