// src/components/IntroSection.js
import React from 'react';
import './IntroSection.css';

const IntroSection = () => {
    return (
        <div className="intro-section">
            <h1 className="big-title">
                Where Next-Gen <span className="highlight">Web 3</span> <br />
                converges with<span className="highlight"> AI </span>
            </h1>
            <p className="sub-title">
                Your very own assistant, helping you navigate through the world of cryptocurrencies.
            </p>
            <div className="get-started-button-wrapper">
                <button className="get-started-button">GET STARTED</button>
            </div>
        </div>
    );
};

export default IntroSection;
