import React, { useEffect } from 'react';
import './App.css'; // Create this CSS file for global styles
import IntroSection from './components/IntroSection';
import './components/EncryptionBackground.css'; // Ensure this file exists and is correct

const App = () => {
  useEffect(() => {
    const cursorReveal = document.createElement('div');
    cursorReveal.className = 'cursor-reveal';
    document.body.appendChild(cursorReveal);

    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      cursorReveal.style.transform = `translate(${clientX - 100}px, ${clientY - 100}px)`;
    };

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.body.removeChild(cursorReveal);
    };
  }, []);

  return (
    <div>
      <IntroSection />
    </div>
  );
};

export default App;
