import React, { useState } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [activeRound, setActiveRound] = useState('Connect');

  const handleHover = (item) => {
    switch(item) {
      case 'Connect':
        setActiveRound('Home');
        break;
      case 'Home':
        setActiveRound('Folder');
        break;
      case 'Folder':
        setActiveRound('Connect');
        break;
      default:
        break;
    }
  };

  return (
    <header id="navbar">
      <nav className="navigation">
        <a 
          href="#home" 
          className={activeRound === 'Home' ? 'round' : ''}
          onMouseEnter={() => handleHover('Home')}
        >
          Home
        </a>
        <a 
          href="#folder" 
          className={activeRound === 'Folder' ? 'round' : ''}
          onMouseEnter={() => handleHover('Folder')}
        >
          Folder
        </a>
        <a 
          href="#connect" 
          className={activeRound === 'Connect' ? 'round' : ''}
          onMouseEnter={() => handleHover('Connect')}
        >
          Connect
        </a>
      </nav>
    </header>
  );
};

export default Navbar;
