import React, { useState } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [activeRound, setActiveRound] = useState('Home');
  const [activeTab, setActiveTab] = useState('Home');

  const handleHover = (item) => {
    switch(item) {
      case 'Connect':
        setActiveRound('Home');
        break;
      case 'Home':
        setActiveRound('Folder');
        break;
      case 'Folder':
        setActiveRound('Connect');
        break;
      default:
        break;
    }
  };

  const handleClick = (item) => {
    setActiveTab(item);
  };

  return (
    <header id="navbar">
      <nav className="navigation">
        <a 
          href="#home" 
          className={`${activeRound === 'Home' ? 'round' : ''} ${activeTab === 'Home' ? 'active' : ''}`}
          onMouseEnter={() => handleHover('Home')}
          onClick={() => handleClick('Home')}
          data-hover="Home"
        >
          <span className="text-wrapper">Home</span>
        </a>
        <a 
          href="#folder" 
          className={`${activeRound === 'Folder' ? 'round' : ''} ${activeTab === 'Folder' ? 'active' : ''}`}
          onMouseEnter={() => handleHover('Folder')}
          onClick={() => handleClick('Folder')}
          data-hover="Folder"
        >
          <span className="text-wrapper">Folder</span>
        </a>
        <a 
          href="#connect" 
          className={`${activeRound === 'Connect' ? 'round' : ''} ${activeTab === 'Connect' ? 'active' : ''}`}
          onMouseEnter={() => handleHover('Connect')}
          onClick={() => handleClick('Connect')}
          data-hover="Connect"
        >
          <span className="text-wrapper">Connect</span>
        </a>
      </nav>
    </header>
  );
};

export default Navbar;
