import React, { useState, useRef, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaFolder, FaConnectdevelop, FaMusic, FaChevronRight } from 'react-icons/fa';
import './Navbar.css';

const Navbar = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);
  const location = useLocation();
  const [activeIndex, setActiveIndex] = useState(0);

  const toggleMusic = (e) => {
    e.preventDefault();
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  useEffect(() => {
    const routes = ['/', '/folder', '/connect', '/music'];
    setActiveIndex(routes.indexOf(location.pathname));
  }, [location]);

  return (
    <div className="navbar-container">
      <nav className="vertical-navbar">
        <Link to="/" className={`nav-item ${activeIndex === 0 ? 'active' : ''}`}>
          <FaHome />
        </Link>
        <Link to="/folder" className={`nav-item ${activeIndex === 1 ? 'active' : ''}`}>
          <FaFolder />
        </Link>
        <Link to="/connect" className={`nav-item ${activeIndex === 2 ? 'active' : ''}`}>
          <FaConnectdevelop />
        </Link>
        <a href="#" onClick={toggleMusic} className={`nav-item ${isPlaying ? 'playing' : ''} ${activeIndex === 3 ? 'active' : ''}`}>
          <FaMusic />
        </a>
      </nav>
      <div className="arrow-indicator" style={{ top: `calc(${activeIndex * 25}% + 10px)` }}>
        <FaChevronRight />
      </div>
      <audio ref={audioRef} src="/AndanteC.mp3" loop />
    </div>
  );
};

export default Navbar;
