import React, { useState } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [activeRound, setActiveRound] = useState('Connect');
  const [activeTab, setActiveTab] = useState('Connect');

  const handleHover = (item) => {
    setActiveRound(item === 'Connect' ? 'Folder' : 'Connect');
  };

  const handleClick = (item) => {
    setActiveTab(item);
  };

  return (
    <header id="navbar">
      <nav className="navigation">
        <a 
          href="#folder" 
          className={`${activeRound === 'Folder' ? 'round' : ''} ${activeTab === 'Folder' ? 'active' : ''}`}
          onMouseEnter={() => handleHover('Folder')}
          onClick={() => handleClick('Folder')}
          data-hover="Folder"
        >
          <span className="text-wrapper">Folder</span>
        </a>
        <a 
          href="#connect" 
          className={`${activeRound === 'Connect' ? 'round' : ''} ${activeTab === 'Connect' ? 'active' : ''}`}
          onMouseEnter={() => handleHover('Connect')}
          onClick={() => handleClick('Connect')}
          data-hover="Connect"
        >
          <span className="text-wrapper">Connect</span>
        </a>
      </nav>
    </header>
  );
};

export default Navbar;
