import React, { useState } from 'react';
import './ProjectsPage.css';

const AboutMe = () => (
  <section className="about-me">
    <h1 className="section-title">About Me</h1>
    <p>
      Hi, I'm Sunda, a finance student & tech enthusiast based in Canada. The main goal of this website is 
      to push myself to build tools & applications not only for experience but for my own personal enjoyment.
    </p>
  </section>
);

const ProjectCard = ({ title, description, link, logo, details, isExpanded, onToggle }) => {
  return (
    <div 
      className={`project-card ${isExpanded ? 'expanded' : ''}`} 
      onClick={onToggle}
    >
      <div className="project-header">
        {logo && <img src={logo} alt={`${title} logo`} className="project-logo" />}
        <h2>{title}</h2>
      </div>
      <p className="project-description">{description}</p>
      <div className="project-details" style={{ maxHeight: isExpanded ? '1000px' : '0', overflow: 'hidden', transition: 'max-height 0.3s ease-out' }}>
        <p>{details}</p>
      </div>
      <a href={link} className="try-it-out" onClick={(e) => e.stopPropagation()}>try it out</a>
    </div>
  );
};

const ProjectsPage = () => {
  const [isStockSenseExpanded, setIsStockSenseExpanded] = useState(false);
  const [isUpcomingProjectExpanded, setIsUpcomingProjectExpanded] = useState(false);

  const projects = [
    {
      title: "StockSense",
      description: "A Python based tool that provides a risk-based rating for your stock portfolio.",
      details: "StockSense uses advanced algorithms to analyze your stock portfolio and provide a comprehensive risk assessment. It takes into account factors such as market volatility, industry trends, and historical performance to give you a clear picture of your investment's risk profile.",
      link: "#",
      logo: "/ss.png",
      isExpanded: isStockSenseExpanded,
      onToggle: () => setIsStockSenseExpanded(!isStockSenseExpanded)
    },
    {
      title: "???",
      description: "Coming soon! An exciting new project is in the works.",
      details: "We're working on something big! This upcoming project will revolutionize the way you interact with financial data. Stay tuned for more information and be prepared to experience finance like never before.",
      link: "#",
      logo: "/signe.png",
      isExpanded: isUpcomingProjectExpanded,
      onToggle: () => setIsUpcomingProjectExpanded(!isUpcomingProjectExpanded)
    }
  ];

  return (
    <div className="projects-page">
      <AboutMe />
      <h1 className="section-title">Projects</h1>
      <div className="projects-container">
        {projects.map((project, index) => (
          <ProjectCard 
            key={index}
            {...project}
          />
        ))}
      </div>
    </div>
  );
};

export default ProjectsPage;
