// src/components/Navbar.js
import React, { useState } from 'react';
import './Navbar.css';
import AudioPlayer from './AudioPlayer'; // Import the AudioPlayer component

const Navbar = ({ scrolled }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isEpic, setIsEpic] = useState(false); // State to toggle epic class

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const toggleEpic = () => {
    setIsEpic(!isEpic);
  };

  return (
    <header id="navbar" className={scrolled ? 'scrolled' : ''}>
      <h2 className="logo">Monat</h2>
      <nav className="navigation">
        <a href="#documentation">Documentation</a>
        <a href="#vision">Vision</a>
        <a href="#tokenomics">Tokenomics</a>
        <a href="#discord">Discord</a>
      </nav>
      <div className="actions">
        <AudioPlayer isPlaying={isPlaying} togglePlayPause={togglePlayPause} />
        <div className={`wallet-popup-wrapper ${isEpic ? 'epic' : ''}`}>
          <button className="wallet-popup" onClick={toggleEpic}>
            Contact Us
          </button>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
