import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [activeItem, setActiveItem] = useState(null);
  const [hoverText, setHoverText] = useState({});

  const navItems = ['Folder', 'Connect'];

  const handleMouseEnter = (item) => {
    setActiveItem(item);
    setHoverText(prev => ({ ...prev, [item]: item.replace(/./g, '*') }));
  };

  const handleMouseLeave = () => {
    setActiveItem(null);
    setHoverText({});
  };

  const encryptChar = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()';
    return chars[Math.floor(Math.random() * chars.length)];
  };

  useEffect(() => {
    if (activeItem) {
      const intervalId = setInterval(() => {
        setHoverText(prev => ({
          ...prev,
          [activeItem]: prev[activeItem].split('').map(char => 
            char === '*' ? encryptChar() : char
          ).join('')
        }));
      }, 50);

      return () => clearInterval(intervalId);
    }
  }, [activeItem]);

  return (
    <header id="navbar">
      <nav className="navigation">
        {navItems.map(item => (
          <Link 
            key={item}
            to={item === 'Folder' ? '/' : `/${item.toLowerCase()}`}
            onMouseEnter={() => handleMouseEnter(item)}
            onMouseLeave={handleMouseLeave}
          >
            <span className="text-wrapper">
              {(hoverText[item] || item).split('').map((char, index) => (
                <span key={index} className="char">
                  {char}
                </span>
              ))}
            </span>
          </Link>
        ))}
      </nav>
    </header>
  );
};

export default Navbar;
