import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import Navbar from './components/Navbar';
import Loader from './components/Loader';
import Home from './components/Home';
import Folder from './components/Folder';
import Connect from './components/Connect';
import './App.css';

const AppContent = () => {
  const location = useLocation();

  return (
    <TransitionGroup>
      <CSSTransition key={location.pathname} classNames="fade" timeout={300}>
        <Routes location={location}>
          <Route path="/" element={<Home />} />
          <Route path="/folder" element={<Folder />} />
          <Route path="/connect" element={<Connect />} />
        </Routes>
      </CSSTransition>
    </TransitionGroup>
  );
};

const App = () => {
  const [loading, setLoading] = useState(true);
  const [opacity, setOpacity] = useState(1);

  useEffect(() => {
    const timer = setTimeout(() => {
      setOpacity(0);
      setTimeout(() => setLoading(false), 1000); // Wait for fade out to complete
    }, 3000); // Adjust this time as needed for initial loading

    return () => clearTimeout(timer);
  }, []);

  return (
    <Router>
      <div className="app-container">
        {loading && (
          <div style={{ opacity: opacity, transition: 'opacity 1s ease-out' }}>
            <Loader />
          </div>
        )}
        {!loading && (
          <div style={{ opacity: 1 - opacity, transition: 'opacity 1s ease-in' }}>
            <Navbar />
            <main className="main-content">
              <AppContent />
            </main>
          </div>
        )}
      </div>
    </Router>
  );
};

export default App;
