import React, { useState, useEffect, useRef } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [activeRound, setActiveRound] = useState('Connect');
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const searchInputRef = useRef(null);

  const handleHover = (item) => {
    setActiveRound(item === 'Connect' ? 'Folder' : 'Connect');
  };

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.key === '/' && !isSearchOpen) {
        event.preventDefault();
        setIsSearchOpen(true);
        setTimeout(() => searchInputRef.current?.focus(), 100);
      } else if (event.key === 'Escape' && isSearchOpen) {
        setIsSearchOpen(false);
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [isSearchOpen]);

  return (
    <>
      <div className="shortcut-hint">Press "/" for company :)</div>
      <header id="navbar">
        <nav className="navigation">
          <a 
            href="#folder" 
            className={activeRound === 'Folder' ? 'round' : ''}
            onMouseEnter={() => handleHover('Folder')}
            data-hover="Folder"
          >
            <span className="text-wrapper">Folder</span>
          </a>
          <a 
            href="#connect" 
            className={`${activeRound === 'Connect' ? 'round' : ''} active`}
            onMouseEnter={() => handleHover('Connect')}
            data-hover="Connect"
          >
            <span className="text-wrapper">Connect</span>
          </a>
        </nav>
        {isSearchOpen && (
          <div className="search-tab">
            <input
              ref={searchInputRef}
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            {/* Add search results here */}
          </div>
        )}
      </header>
    </>
  );
};

export default Navbar;
