// src/components/IntroSection.js
import React, { useEffect, useState } from 'react';
import './IntroSection.css';

const phrases = [
  "Enter your prompt here...",
  "Generate your story...",
  "Create a new idea...",
  "Write your adventure...",
  "Imagine your world..."
];

const IntroSection = () => {
  const [currentPhrase, setCurrentPhrase] = useState('');
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  useEffect(() => {
    const handleTyping = () => {
      const i = loopNum % phrases.length;
      const fullText = phrases[i];

      setCurrentPhrase(isDeleting ? fullText.substring(0, currentPhrase.length - 1) : fullText.substring(0, currentPhrase.length + 1));

      if (!isDeleting && currentPhrase === fullText) {
        setTimeout(() => setIsDeleting(true), 500);
      } else if (isDeleting && currentPhrase === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }

      const speed = isDeleting ? 50 : 150;
      setTypingSpeed(speed);
    };

    const timer = setTimeout(() => handleTyping(), typingSpeed);
    return () => clearTimeout(timer);
  }, [currentPhrase, isDeleting, loopNum, typingSpeed]);

  return (
    <div className="intro-section-container">
      <h1 className="intro-title">
        One prompt away from your imagination
      </h1>
      <div className="prompt-box">
        <span className="prompt-icon">✨</span> {/* Example icon, you can use any other */}
        <input
          type="text"
          className="prompt-input"
          value={currentPhrase}
          readOnly
        />
        <button className="prompt-button">
          Generate
          <span>➔</span> {/* Example arrow icon */}
        </button>
      </div>
    </div>
  );
};

export default IntroSection;
