import React, { useEffect, useState } from 'react';
import './Navbar.css';

const Navbar = () => {
    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setScrolled(window.scrollY > 0);
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <header id="navbar" className={scrolled ? 'scrolled' : ''}>
            <h2 className="logo">Monat</h2>
            <nav className="navigation">
                <span role="button" tabIndex="0" className="nav-item">Documentation</span>
                <span role="button" tabIndex="0" className="nav-item">Vision</span>
                <span role="button" tabIndex="0" className="nav-item">Tokenomics</span>
                <span role="button" tabIndex="0" className="nav-item">Discord</span>
            </nav>
            <div className="wallet-popup-wrapper">
                <button className="wallet-popup">Connect Wallet</button>
            </div>
        </header>
    );
};

export default Navbar;
