// src/components/IntroSection.js
import React, { useRef, useEffect, useState } from 'react';
import './IntroSection.css';

const IntroSection = () => {
  const titleRef = useRef(null);
  const [titleWidth, setTitleWidth] = useState(0);

  useEffect(() => {
    if (titleRef.current) {
      setTitleWidth(titleRef.current.offsetWidth);
    }
  }, [titleRef.current]);

  return (
    <div className="intro-section-container">
      <h1 className="intro-title" ref={titleRef}>
        One prompt away from your imagination
      </h1>
      <div className="prompt-box" style={{ width: titleWidth }}>
        <span className="prompt-icon">✨</span> {/* Example icon, you can use any other */}
        <input
          type="text"
          className="prompt-input"
          placeholder="Enter your prompt here..."
        />
        <button className="prompt-button">
          Generate
          <span>➔</span> {/* Example arrow icon */}
        </button>
      </div>
    </div>
  );
};

export default IntroSection;
