import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const encryptChar = () => {
  const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*()';
  return chars[Math.floor(Math.random() * chars.length)];
};

const Navbar = () => {
  const [hoverStates, setHoverStates] = useState({});
  const navItems = ['Folder', 'Connect'];

  const handleMouseEnter = (item) => {
    setHoverStates(prev => ({ ...prev, [item]: true }));
  };

  const handleMouseLeave = (item) => {
    setHoverStates(prev => ({ ...prev, [item]: false }));
  };

  useEffect(() => {
    const intervals = {};

    navItems.forEach(item => {
      if (hoverStates[item]) {
        let currentText = item.split('').map(() => encryptChar()).join('');
        let targetText = item;
        let index = 0;

        intervals[item] = setInterval(() => {
          if (index < item.length) {
            currentText = currentText.substring(0, index) + targetText[index] + currentText.substring(index + 1);
            setHoverStates(prev => ({ ...prev, [item]: currentText }));
            index++;
          } else {
            clearInterval(intervals[item]);
          }
        }, 50);
      } else if (hoverStates[item] !== undefined) {
        let currentText = item;
        let targetText = item.split('').map(() => encryptChar()).join('');
        let index = 0;

        intervals[item] = setInterval(() => {
          if (index < item.length) {
            currentText = currentText.substring(0, index) + targetText[index] + currentText.substring(index + 1);
            setHoverStates(prev => ({ ...prev, [item]: currentText }));
            index++;
          } else {
            clearInterval(intervals[item]);
            setHoverStates(prev => ({ ...prev, [item]: undefined }));
          }
        }, 50);
      }
    });

    return () => {
      Object.values(intervals).forEach(clearInterval);
    };
  }, [hoverStates]);

  return (
    <header id="navbar">
      <nav className="navigation">
        {navItems.map(item => (
          <Link 
            key={item}
            to={item === 'Folder' ? '/' : `/${item.toLowerCase()}`}
            onMouseEnter={() => handleMouseEnter(item)}
            onMouseLeave={() => handleMouseLeave(item)}
          >
            <span className="text-wrapper">
              {(hoverStates[item] !== undefined ? hoverStates[item] : item).split('').map((char, index) => (
                <span key={index} className="char">
                  {char}
                </span>
              ))}
            </span>
          </Link>
        ))}
      </nav>
    </header>
  );
};

export default Navbar;
