// src/components/IntroSection.js
import React from 'react';
import './IntroSection.css';

const IntroSection = () => {
  return (
    <div className="intro-section-container">
      <div className="intro-section">
        <h1 className="intro-title">
          <span className="static-text">Just one prompt away</span><br />
          <span className="dynamic-text">
            <span>from your imagination</span>
            <span>from brilliance</span>
            <span>from your creation</span>
          </span>
        </h1>
        <div className="prompt-box">
          <span className="prompt-icon">✨</span> {/* Example icon, you can use any other */}
          <input
            type="text"
            className="prompt-input"
            placeholder="Enter your prompt here..."
          />
          <button className="prompt-button">
            Generate
            <span>➔</span> {/* Example arrow icon */}
          </button>
        </div>
      </div>
    </div>
  );
};

export default IntroSection;
