import React, { useState, useEffect, useRef } from 'react';
import './Navbar.css';

const Navbar = () => {
  const [activeRound, setActiveRound] = useState('Connect');
  const [isQuickFindOpen, setIsQuickFindOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const quickFindRef = useRef(null);

  const handleHover = (item) => {
    setActiveRound(item === 'Connect' ? 'Folder' : 'Connect');
  };

  const toggleQuickFind = () => {
    setIsQuickFindOpen(!isQuickFindOpen);
    if (!isQuickFindOpen) {
      setTimeout(() => quickFindRef.current.focus(), 100);
    }
  };

  useEffect(() => {
    const handleKeyPress = (event) => {
      if (event.metaKey && event.key === 'k') {
        event.preventDefault();
        toggleQuickFind();
      }
      if (event.key === 'Escape' && isQuickFindOpen) {
        setIsQuickFindOpen(false);
      }
    };

    document.addEventListener('keydown', handleKeyPress);
    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [isQuickFindOpen]);

  return (
    <header id="navbar">
      <nav className="navigation">
        <a 
          href="#folder" 
          className={activeRound === 'Folder' ? 'round' : ''}
          onMouseEnter={() => handleHover('Folder')}
          data-hover="Folder"
        >
          <span className="text-wrapper">Folder</span>
        </a>
        <a 
          href="#connect" 
          className={`${activeRound === 'Connect' ? 'round' : ''} active`}
          onMouseEnter={() => handleHover('Connect')}
          data-hover="Connect"
        >
          <span className="text-wrapper">Connect</span>
        </a>
        <button onClick={toggleQuickFind} className="quick-find-button">
          Quick Find
        </button>
      </nav>
      {isQuickFindOpen && (
        <div className="quick-find-overlay">
          <div className="quick-find-container">
            <input
              ref={quickFindRef}
              type="text"
              placeholder="Search..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            {/* Add search results here */}
          </div>
        </div>
      )}
    </header>
  );
};

export default Navbar;
