// src/App.js
import React, { useEffect } from 'react';
import Navbar from './components/Navbar';
import IntroSection from './components/IntroSection';
import Loader from './components/Loader';
import './components/EncryptionBackground.css';

const App = () => {
  useEffect(() => {
    const cursorReveal = document.createElement('div');
    cursorReveal.className = 'cursor-reveal';
    document.body.appendChild(cursorReveal);

    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      cursorReveal.style.transform = `translate(${clientX - 100}px, ${clientY - 100}px)`;
    };

    document.addEventListener('mousemove', handleMouseMove);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.body.removeChild(cursorReveal);
    };
  }, []);

  return (
    <div id="encryption-background">
      <div id="binary-overlay"></div>
      <Loader />
      <Navbar />
      <IntroSection />
    </div>
  );
};

export default App;
