import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { FaHome, FaFolder, FaConnectdevelop, FaMusic } from 'react-icons/fa';
import './Navbar.css';

const Navbar = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  const toggleMusic = (e) => {
    e.preventDefault();
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    setIsPlaying(!isPlaying);
  };

  return (
    <nav className="vertical-navbar">
      <Link to="/" className="nav-item">
        <FaHome />
      </Link>
      <Link to="/folder" className="nav-item">
        <FaFolder />
      </Link>
      <Link to="/connect" className="nav-item">
        <FaConnectdevelop />
      </Link>
      <a href="#" onClick={toggleMusic} className={`nav-item ${isPlaying ? 'playing' : ''}`}>
        <FaMusic />
      </a>
      <audio ref={audioRef} src="/AndanteC.mp3" loop />
    </nav>
  );
};

export default Navbar;
