import React, { useState } from 'react';
import './Navbar.css';
import AudioPlayer from './AudioPlayer';

const Navbar = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <header id="navbar" className={menuOpen ? 'menu-open' : ''}>
      <h2 className="logo">Monat</h2>
      <nav className={`navigation ${menuOpen ? 'open' : ''}`}>
        <a href="#documentation">Documentation</a>
        <a href="#vision">Vision</a>
        <a href="#tokenomics">Tokenomics</a>
        <a href="#discord">Discord</a>
      </nav>
      <div className={`actions ${menuOpen ? 'open' : ''}`}>
        <AudioPlayer isPlaying={isPlaying} togglePlayPause={togglePlayPause} />
        <div className="wallet-popup-wrapper">
          <button className="wallet-popup">Contact Us</button>
        </div>
      </div>
      <button className="menu-toggle" onClick={toggleMenu}>
        â˜°
      </button>
    </header>
  );
};

export default Navbar;
